import { INCREASE, DECREASE } from "../action/actionType";

const initialState = 0;

export const counterReducer = (state = initialState, action) => {
  if (action.type === INCREASE) {
    return state + 1;
  } else if (action.type === DECREASE) {
    return state - 1;
  } else {
    return state;
  }
};
