import classes from './Counter.module.css';
import { useDispatch, useSelector } from 'react-redux';
import { increaseByOne, decreaseByOne, increaseByFive, toggle } from '../redux/slice/slice';


function Counter() {
  const dispatch = useDispatch();
  const counter = useSelector((state) => state.counter);
  const show = useSelector((state) => state.showCounter);

  const incrementHandler = () => {
    dispatch(increaseByOne());
  }

  const increaseHandler = () => {
    dispatch(increaseByFive(5));
  }

  const decrementHandler = () => {
    dispatch(decreaseByOne());
  }

  const toggleCounterHandler = () => {
    dispatch(toggle());
  };

  console.log(counter)
  console.log(show)


  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      {counter.showCounter ? <div className={classes.value}>{counter.counter}</div> : null}
      <div>
        <button onClick={incrementHandler}>+1</button>
        <button onClick={increaseHandler}>+5</button>
        <button onClick={decrementHandler}>-1</button>
      </div>
      <button onClick={toggleCounterHandler}>Toggle Counter</button>
    </main>
  );
};

export default Counter;
