import { createSlice } from "@reduxjs/toolkit";

const initialCounterState = { countNum: 0, isCountNum: true };
const initialAuthState = { isAuthenticated: true };

export const counterSlice = createSlice({
  name: 'counter',
  initialState: initialCounterState,
  reducers: {
    increaseByOne(state) {
      state.countNum++;
    },
    decreaseByOne(state) {
      state.countNum--;
    },
    increaseByFive(state, action) {
      state.countNum = state.counter + action.payload;
    },
    toggle(state) {
      state.isCountNum = !state.showCounter;
    }
  }
})

export const authSlice = createSlice({
  name: 'authentication',
  initialState: initialAuthState,
  reducers: {
    login(state) {
      state.isAuthenticated = true;
    },
    logout(state) {
      state.isAuthenticated = false;
    }
  }
})

export const { increaseByOne, decreaseByOne, increaseByFive, toggle } = counterSlice.actions;
export const { login, logout } = authSlice.actions;
